package statements;

public class Ifelseif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int amount=100;
if (amount>300)
{
	System.out.println("You can travel to Panvel");
	
}
 else if (amount>100)
{
	System.out.println("You can travel upto Lonavala");
	}
 else if (amount>50)
    {
	 System.out.println("You can travel upto Talegaon");
    }
	
	else
	{
		System.out.println("No travelling is possible for me");
	}
	}
}
	


